import { combineReducers, legacy_createStore } from "redux";
import { cartReducer, openCartReducer } from "./reducers/cartReducers";
const rootReducer = combineReducers({
  cartReducer,
  openCartReducer,
});

export const store = legacy_createStore(rootReducer);
